// src/components/FormItem.js
import React, { useRef, useEffect } from 'react';

import { 
    Box, 
    TextField, 
    IconButton, 
    Button, 
    Typography, Grid
  } from '@material-ui/core/';
  import DeleteIcon from '@material-ui/icons/Delete';

  
  import SweetAlert from "react-bootstrap-sweetalert";

// Función auxiliar para establecer valores anidados en un objeto
const setNestedValue = (obj, path, value) => {
    const keys = path.replace(/\[(\d+)\]/g, '.$1').split('.');
    let current = { ...obj };
    let temp = current;

    keys.forEach((key, index) => {
        if (index === keys.length - 1) {
            temp[key] = value;
        } else {
            if (!temp[key]) {
                // Determina si el siguiente key es un índice numérico para manejar arrays
                temp[key] = isNaN(keys[index + 1]) ? {} : [];
            } else {
                temp[key] = Array.isArray(temp[key]) ? [...temp[key]] : { ...temp[key] };
            }
            temp = temp[key];
        }
    });

    return current;
};

const FormItem = ({ index, form, updateForm, removeForm, dataJson }) => {
    const fileInputRef = useRef(null);
    const [error, setError] = React.useState(null);

    useEffect(() => {
      handleJsonUpload();
    },[])

    const handleChange = (path, value) => {
        const updatedForm = setNestedValue(form, path, value);
        updateForm(index, updatedForm);
    };

    const addCompareciente = () => {
        const currentComparecientes = form.minuta?.protocolizacion?.comparecientes || [];
        const newComparecientes = [...currentComparecientes, {
            fideicomiso: {
                nombre_fideicomiso: '',
                nit_fideicomiso: '',
                representante_fideicomiso: {
                    nombre_apoderada: '',
                    edad_apoderada: '',
                    domicilio_apoderada: '',
                    cedula_apoderada: '',
                    ciudad_cedula_apoderada: ''
                },
                fiduciaria: {
                    nombre_fiduciaria: '',
                    nit_fiduciaria: ''
                },
                escritura_fiduciaria: {
                    numero_escritura: '',
                    fecha_escritura: '',
                    notaria_escritura: '',
                    domicilio_fiduciaria: ''
                },
                representante_legal_fideicomiso: {
                    nombre_representante: '',
                    cedula_representante: '',
                    cargo_representante: ''
                },
                fideicomitente_desarrollador: {
                    representante_legal: {
                        nombre_representante: '',
                        edad_representante: '',
                        ciudad_representante: '',
                        cedula_representante: '',
                        ciudad_expedicion_cedula: '',
                        calidad_representante: ''
                    },
                    sociedad: {
                        nombre_sociedad_actual: '',
                        nombre_sociedad_anterior: '',
                        nit_sociedad: '',
                        domicilio_sociedad: '',
                        numero_escritura_constitucion: '',
                        fecha_escritura_constitucion: '',
                        notaria_constitucion: '',
                        matricula_mercantil: '',
                        camara_comercio_inscripcion: '',
                        fecha_inscripcion: '',
                        numero_inscripcion_libro: '',
                        ultima_reforma_escritura: {
                            numero_escritura_reforma: '',
                            fecha_escritura_reforma: '',
                            notaria_reforma: ''
                        },
                        ultima_inscripcion_reforma: {
                            numero_escritura_ultima: '',
                            fecha_escritura_ultima: '',
                            notaria_ultima: '',
                            fecha_inscripcion_ultima: '',
                            numero_inscripcion_ultima: ''
                        }
                    }
                }
            }
        }];
        handleChange('minuta.protocolizacion.comparecientes', newComparecientes);
    };

    const removeCompareciente = (i) => {
        const currentComparecientes = form.minuta?.protocolizacion?.comparecientes || [];
        const newComparecientes = currentComparecientes.filter((_, idx) => idx !== i);
        handleChange('minuta.protocolizacion.comparecientes', newComparecientes);
    };

    const addComprador = () => {
        const currentCompradores = form.minuta?.compradores || [];
        const newCompradores = [...currentCompradores, {
            nombre_comprador: '',
            domicilio_comprador: '',
            direccion_domicilio_comprador: '',
            telefono_comprador: '',
            Actividad_economica: '',
            cedula_comprador: '',
            estado_civil_comprador: '',
            apoderado: {
                nombre_apoderado: '',
                domicilio_apoderado: '',
                numero_documento_apoderado: '',
                tipo_documento_apoderado: ''
            }
        }];
        handleChange('minuta.compradores', newCompradores);
    };

    const removeComprador = (i) => {
        const currentCompradores = form.minuta?.compradores || [];
        const newCompradores = currentCompradores.filter((_, idx) => idx !== i);
        handleChange('minuta.compradores', newCompradores);
    };

    const handleJsonUpload = (event) => {
        //const file = event.target.files[0];
        //if (file) {
            //const reader = new FileReader();
            //reader.onload = (e) => {
                try {
                    const jsonData = dataJson;
                    // Validar que el JSON tenga la estructura esperada
                    if (jsonData.minuta) {
                        updateForm(index, jsonData);
                        setError(null);
                    } else {
                        setError('El JSON no contiene la sección "minuta".');
                    }
                } catch (err) {
                    setError('Error al parsear el archivo JSON. Asegúrate de que el archivo sea válido.');
                }
            //};
            //reader.readAsText(file);
            // Resetear el input para permitir cargar el mismo archivo nuevamente si es necesario
            //event.target.value = null;
        //}
    };

    const triggerFileInput = () => {
        fileInputRef.current.click();
    };

    return (
      <div style={{ border: '1px solid #ddd', padding: '20px', marginBottom: '20px', borderRadius: '5px' }}>
        <Box style={{ marginBottom: '20px' }}>
          <Grid container justify="space-between" alignItems="center" style={{ marginBottom: '20px' }}>
            <Typography variant="h4">Formulario #{index + 1}</Typography>
            <Button onClick={() => removeForm(index)} style={{ backgroundColor: '#f8d7da', color: '#721c24' }}>
              Eliminar Formulario
            </Button>
          </Grid>
  
          {/* Botón para Cargar JSON */}
          <Grid container spacing={2} style={{ marginBottom: '20px' }}>
            <Grid item>
              <Button onClick={triggerFileInput}>Cargar JSON</Button>
              <input
                type="file"
                accept=".json"
                ref={fileInputRef}
                style={{ display: 'none' }}
                onChange={handleJsonUpload}
              />
            </Grid>
            {error && (
              <Grid item xs={12}>
                <SweetAlert>{error}</SweetAlert>
              </Grid>
            )}
          </Grid>
  
          {/* Deed Information Section */}
          <Grid container direction="column" spacing={2}>
            <Grid item>
              <TextField
                label="Deed Number"
                value={form.deed_number || ''}
                onChange={(e) => handleChange('deed_number', e.target.value)}
                fullWidth
                required
              />
            </Grid>
            <Grid item>
              <TextField
                label="Deed Date"
                value={form.deed_date || ''}
                onChange={(e) => handleChange('deed_date', e.target.value)}
                fullWidth
                required
              />
            </Grid>
            <Grid item>
              <TextField
                label="Sale Value"
                value={form.sale_value || ''}
                onChange={(e) => handleChange('sale_value', e.target.value)}
                fullWidth
                required
              />
            </Grid>
            <Grid item>
              <TextField
                label="Appraisal Value"
                value={form.appraisal_value || ''}
                onChange={(e) => handleChange('appraisal_value', e.target.value)}
                fullWidth
                required
              />
            </Grid>
            <Grid item>
              <TextField
                label="Advance Value"
                value={form.advance_value || ''}
                onChange={(e) => handleChange('advance_value', e.target.value)}
                fullWidth
                required
              />
            </Grid>
            <Grid item>
              <TextField
                label="Certificate Number"
                value={form.certificate_number || ''}
                onChange={(e) => handleChange('certificate_number', e.target.value)}
                fullWidth
                required
              />
            </Grid>
            <Grid item>
              <TextField
                label="Certificate Date"
                value={form.certificate_date || ''}
                onChange={(e) => handleChange('certificate_date', e.target.value)}
                fullWidth
                required
              />
            </Grid>
          </Grid>
  
          {/* Buyers Section */}
          <Typography style={{ fontWeight: 'bold', marginTop: '20px', marginBottom: '10px' }}>Buyers</Typography>
          <Grid container direction="column" spacing={2}>
            {form.buyers && form.buyers.map((buyer, i) => (
              <Box key={i} style={{ border: '1px solid #ccc', padding: '15px', borderRadius: '5px', marginTop: '10px' }}>
                <Grid container justify="space-between" alignItems="center" style={{ marginBottom: '10px' }}>
                  <Typography variant="h6">Buyer {i + 1}</Typography>
                  <IconButton
                    onClick={() => handleChange(`buyers[${i}]`, null)}
                    style={{ backgroundColor: '#f8d7da', color: '#721c24' }}
                    aria-label="Delete Buyer"
                  >
                    <DeleteIcon />
                  </IconButton>
                </Grid>
  
                <Grid container spacing={2}>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      label="Buyer Name"
                      value={buyer.name || ''}
                      onChange={(e) => handleChange(`buyers[${i}].name`, e.target.value)}
                      fullWidth
                      required
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      label="Buyer ID Number"
                      value={buyer.id_number || ''}
                      onChange={(e) => handleChange(`buyers[${i}].id_number`, e.target.value)}
                      fullWidth
                      required
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      label="Buyer Address"
                      value={buyer.address || ''}
                      onChange={(e) => handleChange(`buyers[${i}].address`, e.target.value)}
                      fullWidth
                      required
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      label="Buyer Phone"
                      value={buyer.phone || ''}
                      onChange={(e) => handleChange(`buyers[${i}].phone`, e.target.value)}
                      fullWidth
                      required
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      label="Civil Status"
                      value={buyer.civil_status || ''}
                      onChange={(e) => handleChange(`buyers[${i}].civil_status`, e.target.value)}
                      fullWidth
                      required
                    />
                  </Grid>
                  {buyer.has_attorney === 'si' && (
                    <>
                      <Typography variant="subtitle1" style={{ marginTop: '15px' }}>Attorney Information</Typography>
                      <Grid container spacing={2}>
                        <Grid item xs={12} sm={6}>
                          <TextField
                            label="Attorney Name"
                            value={buyer.attorney?.name || ''}
                            onChange={(e) => handleChange(`buyers[${i}].attorney.name`, e.target.value)}
                            fullWidth
                            required
                          />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                          <TextField
                            label="Attorney Domicile"
                            value={buyer.attorney?.domicile || ''}
                            onChange={(e) => handleChange(`buyers[${i}].attorney.domicile`, e.target.value)}
                            fullWidth
                            required
                          />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                          <TextField
                            label="Attorney Document Type"
                            value={buyer.attorney?.document_type || ''}
                            onChange={(e) => handleChange(`buyers[${i}].attorney.document_type`, e.target.value)}
                            fullWidth
                            required
                          />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                          <TextField
                            label="Attorney Document ID"
                            value={buyer.attorney?.document_id || ''}
                            onChange={(e) => handleChange(`buyers[${i}].attorney.document_id`, e.target.value)}
                            fullWidth
                            required
                          />
                        </Grid>
                      </Grid>
                    </>
                  )}
                </Grid>
              </Box>
            ))}
          </Grid>
        </Box>
      </div>
    );
}

export default FormItem;
